mapper.select:
SELECT created_at
FROM chat_message
WHERE id = #{messageId}

SELECT *
FROM (
SELECT
cm.*,
e.name AS sender_name
FROM
chat_message cm
LEFT JOIN employee e ON cm.sender_id = e.id
WHERE
cm.workspace_id = #{workspaceId}
AND cm.channel_id = #{channelId}
AND cm.created_at < #{beforeTime}
ORDER BY cm.created_at DESC
)
WHERE ROWNUM <= #{limit}

SELECT COUNT(*)
FROM CHAT_MESSAGE_LIKE
WHERE MESSAGE_ID = #{parentId}
AND EMPLOYEE_ID = #{senderId}
AND workspace_id = #{workspaceId}
and channel_id = #{channelId}

SELECT COUNT(*)
FROM CHAT_MESSAGE_LIKE
WHERE MESSAGE_ID = #{id}
AND workspace_id = #{workspaceId}
and channel_id = #{channelId}

SELECT employee_id
FROM CHAT_MESSAGE_LIKE
WHERE MESSAGE_ID = #{id}
AND workspace_id = #{workspaceId}
and channel_id = #{channelId}

mapper.delete:
DELETE FROM CHAT_MESSAGE_LIKE
WHERE MESSAGE_ID = #{parentId}
AND EMPLOYEE_ID = #{senderId}
AND workspace_id = #{workspaceId}
and channel_id = #{channelId}

mapper.select:
SELECT COUNT(*)
FROM CHAT_MESSAGE_REACTION
WHERE MESSAGE_ID = #{parentId}
AND EMPLOYEE_ID = #{senderId}
AND workspace_id = #{workspaceId}
and channel_id = #{channelId}
and emoji = #{emoji}

SELECT EMOJI, EMPLOYEE_ID
FROM CHAT_MESSAGE_REACTION
WHERE MESSAGE_ID = #{messageId}

mapper.delete:
DELETE FROM CHAT_MESSAGE_REACTION
WHERE MESSAGE_ID = #{parentId}
AND EMPLOYEE_ID = #{senderId}
AND workspace_id = #{workspaceId}
and channel_id = #{channelId}
AND EMOJI = #{emoji}

mapper.select:
SELECT *
FROM chat_file
WHERE workspace_id = #{workspaceId}
AND channel_id = #{channelId}
ORDER BY created_at DESC
