mapper.select:
SELECT *
FROM work_plan
WHERE employee_id = #{employeeId}
AND plan_date BETWEEN #{startDate} AND #{endDate}
ORDER BY plan_date

mapper.insert:
INSERT INTO work_plan (
plan_id, employee_id, plan_date, start_datetime, end_datetime, type, status, created_at
) VALUES (
work_plan_seq.NEXTVAL, #{employeeId}, #{planDate}, #{startDatetime},
#{endDatetime}, #{type}, #{status}, sysdate
)

mapper.delete:
DELETE FROM work_plan
WHERE employee_id = #{employeeId}
AND plan_date = #{planDate}

mapper.select:
SELECT COUNT(*)
FROM work_plan
WHERE employee_id = #{employeeId}
AND plan_date = #{planDate}
